import React, { useState } from 'react';

const MovieCard = ({ movie }) => {
  const [showTrailer, setShowTrailer] = useState(false);

  const toggleTrailer = () => {
    setShowTrailer(!showTrailer);
  };

  return (
    <div className="movie">
      <div>
        <p>{movie.Year}</p>
      </div>
      <div>
        <img
          src={
            movie.Poster !==
            'https://m.media-amazon.com/images/M/MV5BYzYzZDViNWYtNWViMS00NDMxLThlN2YtZjFkOWMwODkzNzhiXkEyXkFqcGdeQXVyMTUwMzM4NzU0._V1_SX300.jpg'
              ? movie.Poster
              : 'https://via.placeholder.com/400'
          }
          alt={movie.title}
        />
      </div>

      <div>
        <span>{movie.Type}</span>
        <h3>{movie.Title}</h3>
      </div>

      <button onClick={toggleTrailer}>Play Trailer</button>

      {showTrailer && (
        <div className="trailer-popup">
          <div className="video-player">
            <iframe
              src={movie.TrailerUrl}
              title="Movie Trailer"
              frameborder="0"
              allowFullScreen
            ></iframe>
          </div>
          <button onClick={toggleTrailer}>Close</button>
        </div>
      )}
    </div>
  );
};

export default MovieCard;
